import React, { useState } from 'react';
import errorMessages from './error-messages';
import { fetchSendMessage } from './services';

const SendMessage = ({onRefresh }) => {

  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const performAction = () => {
    if(!message) {
      setError(errorMessages.MESSAGE_REQUIRED);
      return;
    }
    setError('');
    fetchSendMessage(message)
    .then( (usersMessagesList) => {
      setMessage('');
      onRefresh(usersMessagesList.messagesList, usersMessagesList.usersList, error);
    })
    .catch( (err) => {
      setMessage('');
      setError(errorMessages[err.code || 'DEFAULT']);
    });
  };

  return (
    <div className="send-message">
      <input className="to-send-text" value={message} onChange={ (e) => setMessage(e.target.value) }/>
      <button className="send-message-button" onClick={ performAction }>Send</button>
    </div>
  );

}
export default SendMessage
